sql.where:
d.DEL_FLAG = 0
and (d.NICKNAME like LOWER(CONCAT('%',#{findContent,jdbcType=VARCHAR},'%')) or d.PHONE like LOWER(CONCAT('%',#{findContent,jdbcType=VARCHAR},'%')) or v.NAME = #{findContent,jdbcType=VARCHAR})
and d.INVITAION_CODE = #{inviteCode}

mapper.select:
select 
from  tb_vip d
where d.DEL_FLAG = 0
AND d.PHONE = #{phone}

  AND d.PASSWORD = #{password}

mapper.update:
update tb_vip t
set
END_TIME = DATE_ADD(END_TIME, INTERVAL (

timestampdiff(SECOND,case when timestampdiff(SECOND,t.CRT_TIME,(select s.BGN_TIME from tb_stop_date s)) > 0 then (select s.BGN_TIME from tb_stop_date s) else t.CRT_TIME end ,now())
) second)
where LEVEL != 0 and DEL_FLAG = 0;

mapper.select:
select timestampdiff(MINUTE ,now(),DATE_ADD(END_TIME, INTERVAL (
timestampdiff(SECOND,case when timestampdiff(SECOND,t.CRT_TIME,(select s.BGN_TIME from tb_stop_date s)) > 0 then (select s.BGN_TIME from tb_stop_date s) else t.CRT_TIME end ,now())
) second)) surplus_minute  from tb_vip t  where t.DEL_FLAG = 0 and t.LEVEL != 0 and t.phone = #{phone} ;

select ,v.NAME as belong,v2.NAME AS belong2 from tb_vip d LEFT JOIN tb_dealer v ON v.INVITE_NUM = d.INVITAION_CODE LEFT JOIN tb_dealer v2 ON v.parent_id= v2.FK_USER_ID
AND v.INVITE_NUM IN (SELECT INVITE_NUM FROM tb_dealer WHERE SEAT_NUM = (SELECT SEAT_NUM FROM tb_dealer WHERE FK_USER_ID=#{userId}))
LIMIT #{start},#{pageSize}

select ,v.NAME as belong,v2.NAME AS belong2 from tb_vip d LEFT JOIN tb_dealer v ON v.INVITE_NUM = d.INVITAION_CODE LEFT JOIN tb_dealer v2 ON v.parent_id= v2.FK_USER_ID
and v.fk_user_id = #{parentId}
LIMIT #{start},#{pageSize}
